bool oled_task_user(void) {
    if (is_keyboard_master()) {
        switch (get_highest_layer(layer_state)) {
            case _XMLBV:
                render_animation();
                break;
            case _ACTV8:
                oled_set_cursor(0, 0);
                oled_write("                  A         C         T         V         8", false);
                break;
            case _OPER8:
                oled_set_cursor(0, 0);
                oled_write("                  O         P         E         R         8", false);
                break;
            case _NAVG8:
                oled_set_cursor(0, 0);
                oled_write("                  N         A         V         G         8", false);
                break;
        }
    } else {
        render_animation();
    }
    return false;
}
#endif // OLED_ENABLE

bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    if (record->event.pressed) {
#ifdef OLED_ENABLE
        set_keylog(keycode, record);
#endif
        // set_timelog();
    }
    return true;
}
